/*
id_shop	uri	domain	main
1	/	presta:8000	1
*/
SELECT s.id_shop
, CONCAT(su.physical_uri, su.virtual_uri) AS uri
, su.domain
, su.main 
FROM ps_shop_url su 
LEFT JOIN ps_shop s ON (s.id_shop = su.id_shop) 
WHERE (su.domain = 'presta:8000'
OR su.domain_ssl = 'presta:8000') 
AND s.active = 1 
AND s.deleted = 0 
ORDER BY LENGTH(CONCAT(su.physical_uri, su.virtual_uri)) DESC

-- 1
/*
+---------------+---------+----------------+-------------+-------------+--------+---------+---------+---------------+------------------------+-------------+------------+--------+---------+------------+------------------------+--------+-------------+-------------+--------------+-------------+
| id_shop_group |  name   | share_customer | share_order | share_stock | active | deleted | id_shop | id_shop_group |          name          | id_category | theme_name | active | deleted | group_name |       shop_name        | active |   domain    | domain_ssl  | physical_uri | virtual_uri |
+---------------+---------+----------------+-------------+-------------+--------+---------+---------+---------------+------------------------+-------------+------------+--------+---------+------------+------------------------+--------+-------------+-------------+--------------+-------------+
|             1 | Default |              0 |           0 |           0 |      1 |       0 |       1 |             1 | el-nombre-de-la-tienda |           2 | classic    |      1 |       0 | Default    | el-nombre-de-la-tienda |      1 | presta:8000 | presta:8000 | /            |             |
+---------------+---------+----------------+-------------+-------------+--------+---------+---------+---------------+------------------------+-------------+------------+--------+---------+------------+------------------------+--------+-------------+-------------+--------------+-------------+
*/
SELECT gs.*
, s.*
, gs.name AS group_name
, s.name AS shop_name
, s.active
, su.domain
, su.domain_ssl
, su.physical_uri
, su.virtual_uri 
FROM ps_shop_group gs 
LEFT JOIN ps_shop s ON s.id_shop_group = gs.id_shop_group 
LEFT JOIN ps_shop_url su ON s.id_shop = su.id_shop
AND su.main = 1 
WHERE s.deleted = 0 
AND gs.deleted = 0 
ORDER BY gs.name, s.name

