| physical_uri | virtual_uri |   domain    | domain_ssl  |
+--------------+-------------+-------------+-------------+
| /            |             | presta:8000 | presta:8000 |
+--------------+-------------+-------------+-------------+
*/
SELECT su.physical_uri, su.virtual_uri, su.domain, su.domain_ssl 
FROM ps_shop s 
LEFT JOIN ps_shop_url su 
ON (s.id_shop = su.id_shop) 
WHERE s.id_shop = 1 
AND s.active = 1
AND s.deleted = 0
AND su.main = 1 LIMIT 1

